export default class Utility
{
    /**
     * Smoothly scroll to a position on the page
     * @param {*} n - The amount to scroll on the page
     */
    static scroll(n)
    {
        window.scrollTo({
                top: {n},
                behavior: "smooth"
            }
        );

        return;
    }
}
