export default class Utility
{
    /**
     * Smoothly scroll to a position on the page
     * @param {*} n - The amount to scroll on the page from the top
     */
    static scrollToElement(elem)
    {
        window.scrollTo({
                top: document.getElementById(elem).getBoundingClientRect().top + window.pageYOffset,
                behavior: "smooth"
            }
        );
    }
}
