export default class Utility
{
    /**
     * Smoothly scroll to an element on the page
     * @param {*} elem - The id of the element to scroll to
     */
    static scrollToElement(elem)
    {
        window.scrollTo({
                top: document.getElementById(elem).getBoundingClientRect().top + window.pageYOffset,
                behavior: "smooth"
            }
        );
    }

    /**
     * Smoothly scroll to an element on the page (Overload 2/2)
     * @param {*} elem - The id of the element to scroll to
     * @param {*} offset - The offset
     */
    static scrollToElement(elem, offset)
    {
        window.scrollTo({
                top: (document.getElementById(elem).getBoundingClientRect().top + window.pageYOffset) + offset,
                behavior: "smooth"
            }
        );
    }
}
